definition.script:
pipeline {
    agent any // ใช้ agent ใดก็ได้
    stages {
        stage('Build') {
            steps {
                echo 'Building...'
                // คำสั่งสำหรับการ build เช่น การ compile โค้ด
            }
        }
        stage('Test') {
            steps {
                echo 'Testing...'
                // คำสั่งสำหรับการทดสอบ เช่น การรัน unit tests
            }
        }
        stage('Deploy') {
            steps {
                echo 'Deploying...'
                // คำสั่งสำหรับการ deploy เช่น การส่งโค้ดไปยังเซิร์ฟเวอร์
            }
        }
    }
}
